const filterMachineData = (data) => {
    const result = {};
    data.forEach(item => {
        // Kita simpan sebagai objek agar bisa menampung banyak info
        result[item.MchNumber.toString()] = {
            mchId: item.MchID,
            ip: item.ipOPC
        };
    });
    return result;
};

// Ambil data dari query MSSQL
const inputData = msg.payload;

// Proses data
const result = filterMachineData(inputData);

// Set ke payload untuk debug (opsional)
msg.payload = result;

// Simpan ke Global Variable
global.set("MchID_MST_G_HAITIAN", result);

return msg;

const mchId = flow.get("last_mch_id");
const ip = flow.get("last_ip");

if (mchId && msg.payload !== undefined && !msg.endpoint) {
    const globalVarName = "Cache_" + mchId;
    let machineData = global.get(globalVarName) || {};

    // 1. Ambil Master Map yang berisi Judul & Divisor
    const paramMaster = global.get("HaitianZE3600_Parameter_Master") || {};

    const nodeId = msg.topic;
    const rawValue = msg.payload;

    // 2. Ambil metadata berdasarkan NodeId
    const meta = paramMaster[nodeId] || { title: "Unknown", divisor: 1, unit: "" };

    // 3. Kalkulasi Nilai Akhir
    const finalValue = rawValue / meta.divisor;

    // 4. Update data mesin dengan struktur yang lebih lengkap
    // Kita simpan value dan title agar JSON di SQL nanti lebih informatif
    machineData[nodeId] = {
        title: meta.title,
        value: finalValue,
        unit: meta.unit,
        raw: rawValue
    };

    machineData["last_sync"] = new Date().toISOString();
    machineData["ip"] = ip;

    global.set(globalVarName, machineData);

    msg.payload = {
        mchId: mchId,
        nodeId: nodeId,
        detail: machineData[nodeId]
    };

    node.status({fill:"green", shape:"dot", text: mchId + ": " + meta.title + " updated"});
    return msg;
}
return null;

const masterData = global.get("MchID_MST_G_HAITIAN") || {};
const endpoint = msg.endpoint || "";
const ipMatch = endpoint.match(/\/\/(.*?):/);
const ip = ipMatch ? ipMatch[1] : "0.0.0.0";

let machine_id = "Unknown";
for (let key in masterData) {
    if (masterData[key].ip === ip) {
        machine_id = masterData[key].mchId;
        break;
    }
}

flow.set("last_mch_id", machine_id);
flow.set("last_ip", ip);

return msg;

// 1. Ambil nama variabel global asal (Cache_...)
// Gunakan optional chaining atau fallback agar tidak error
const mchId = msg.payload ? msg.payload.mchId : null;

if (!mchId) {
    node.error("mchId tidak ditemukan dalam msg.payload!");
    return null;
}

const sourceGlobalName = "Cache_" + mchId;
const targetGlobalName = "paramset_" + mchId;

// 2. Ambil data mentah dari global cache
const machineData = global.get(sourceGlobalName);

if (machineData) {
    let cleanParamSet = {};

    for (let nodeId in machineData) {
        // Abaikan metadata
        if (nodeId !== "last_sync" && nodeId !== "ip") {
            const item = machineData[nodeId];
            if (item && item.title) {
                // Gunakan nama Title sebagai Key
                cleanParamSet[item.title] = item.value;
            }
        }
    }

    // 3. SIMPAN ke Global Variable (Object)
    global.set(targetGlobalName, cleanParamSet);

    // 4. Siapkan payload untuk SQL
    msg.payload = {
        machineId: mchId,
        paramset: JSON.stringify(cleanParamSet)
    };

    node.status({ fill: "blue", shape: "dot", text: "Stored: " + targetGlobalName });
    return msg;
} else {
    node.warn("Source Global " + sourceGlobalName + " masih kosong!");
    node.status({ fill: "red", shape: "ring", text: sourceGlobalName + " empty" });
    return null;
}

const globalKeys = global.keys();
const paramsetKeys = globalKeys.filter(key => key.startsWith("paramset_"));

if (paramsetKeys.length === 0) {
    node.status({ fill: "red", shape: "ring", text: "No data to insert" });
    return null;
}

let messages = [];

paramsetKeys.forEach(key => {

    const mchId = key.replace("paramset_", "");
    const cleanData = global.get(key);

    if (cleanData && Object.keys(cleanData).length > 0) {

        const paramsetString = JSON.stringify(cleanData).replace(/'/g, "''");

        const sql = `
        INSERT INTO MachineParameterSettingTRX
        (
            machineId,
            paramset,
            material_id,
            material_name,
            cavity,
            created_at
        )
        SELECT TOP 1
            '${mchId}',
            '${paramsetString}',
            c.material_id,
            c.material_name,
            t.actual_cvt,
            CAST(SYSDATETIMEOFFSET() AT TIME ZONE 'SE Asia Standard Time' AS DATETIME)
        FROM iot.dbo.countboard_tasks t
        JOIN IoT.dbo.machinemst m WITH (NOLOCK) 
            ON m.MchDesc = t.machine_name
        JOIN IoT.dbo.coois c WITH (NOLOCK) 
            ON c.po_name = t.po_name 
            AND ISNULL(c.is_deleted,0)=0
        WHERE m.MchID = '${mchId}'
        ORDER BY t.created_at DESC, t.id DESC
        `;

        messages.push({
            payload: sql
        });
    }
});

if (messages.length > 0) {
    node.status({ fill: "green", shape: "dot", text: "Prepared " + messages.length + " machines" });
    return [messages];
} else {
    node.status({ fill: "yellow", shape: "ring", text: "Data keys exist but objects empty" });
    return null;
}

