});

if (messages.length > 0) {
    node.status({ fill: "green", shape: "dot", text: "Prepared " + messages.length + " machines" });
    // Mengembalikan array di dalam array untuk Bulk Messaging
    return [messages];
} else {
    node.status({ fill: "yellow", shape: "ring", text: "Data keys exist but objects are empty" });
    return null;
}

