// 1. Ambil Master Data dari Global
const masterData = global.get("MchID_MST_G_HAITIAN") || {};

// 2. Ekstrak IP dari endpoint
const endpoint = msg.endpoint || "opc.tcp://unknown:4842";
const ipMatch = endpoint.match(/\/\/(.*?):/);
const ip = ipMatch ? ipMatch[1] : "0.0.0.0";

// 3. Inisialisasi variabel untuk menampung mchId yang ditemukan
let machine_id = null;

// 4. Lakukan pencarian berdasarkan IP di dalam Master Data
for (let key in masterData) {
    if (masterData[key].ip === ip) {
        machine_id = masterData[key].mchId; // Ambil mchId jika IP cocok
        break; // Berhenti looping jika sudah ketemu
    }
}

// 5. Masukkan ke dalam payload
msg.payload = {
    timestamp: new Date().toISOString(),
    machine_id: machine_id,
    ip: ip,
    endpoint: endpoint
};

return msg;
